import { Container, Row, Col, ListGroup, ListGroupItem, Button } from "react-bootstrap";
import { StarFill } from "react-bootstrap-icons";
import { connect } from "react-redux";
import { removeFromFav } from "../redux/actions";

const mapDispatchToProps = (dispatch) => ({
  removeFromFav: (f) => {
    dispatch(removeFromFav(f));
  },
});

const Favourites = ({ favourites, removeFromFav }) => {
  return (
    <Container>
      <Row>
        <Col xs={12}>
          <h1 className="mt-3 pb-3">FAVORITE JOBS</h1>
          <ListGroup>
            {favourites &&
              favourites.map((STAR, i) => (
                <ListGroupItem key={i}>
                  <span className="m-auto">{STAR}</span>
                  <Button className="text-left" variant="outline-danger" size="sm" onClick={() => removeFromFav(STAR)}>REMOVE</Button>
                </ListGroupItem>
              ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default connect((s) => s, mapDispatchToProps)(Favourites);
