import { Row, Col, Button } from 'react-bootstrap'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { useNavigate } from "react-router-dom";

const mapStateToProps = state => {
      return {state}
}


const mapDispatchToProps = dispatch => {
      return {

      addToFavorites: company => {
            dispatch({ 
            type: "ADD_TO_FAV",
            payload: company,
            })
}}
}

const addFavorite = (props) => {
      addFavorite(props.data.company_name)

}

const Job = ( props ) => {
      const navigate = useNavigate();

      return (
            <Row
            className="mx-0 mt-3 p-3"
            style={{ border: '1px solid #00000033', borderRadius: 4 }}
            >
            <Col xs={3}>
                  <Link to={`/${props.data.company_name}`}>{props.data.company_name}</Link>
            </Col>
            <Col xs={6}>
                  <Link to={props.data.url} target="_blank" rel="noreferrer">
                  {props.data.title}
                  </Link>
            </Col>
            <Col xs={3}>
            <Button onClick={() => navigate("/favourites")}
            variant="success">Add to favorite</Button>
            </Col>
            </Row>
)
}

export default connect(mapStateToProps, mapDispatchToProps)(Job)
